:root {
  --color-accent: #d3221e;
  --color-primary: #0d0d0d;
  --color-secondary: #1d1d1d;
  --color-shadow: #4c5d5a;
  --text-primary: #fcf4f4;
  --outline-color: rgba(255, 255, 255, 0.1);
}

*,
*::after,
*::before {
  box-sizing: border-box;
}

/* Topography */
html {
  font-size: 62.5%;
}

body {
  font-family: "Montserrat", sans-serif;
  line-height: 1.5;
  font-size: 2.2rem;
  color: #fff;
  /* height: 200vh; */
  margin: 0;
}

h1,
h2,
h3 {
  color: var(--color-headings);
  margin-bottom: 2rem;
}

h1 {
  font-size: 7rem;
}

h2 {
  font-size: 4rem;
}

h3 {
  font-size: 3rem;
}

p {
  margin-top: 0;
}

@media screen and (min-width: 1024px) {
  body {
    font-size: 1.8rem;
  }

  h1 {
    font-size: 8rem;
  }

  h2 {
    font-size: 4rem;
  }

  h3 {
    font-size: 2.4rem;
  }
}

li {
  list-style: none;
}

.gloss {
  border: 1px solid rgba(255, 255, 255, 0.15);
  backdrop-filter: blur(10px);
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.2),
    inset 0 1px 1px rgba(255, 255, 255, 0.25);
  background: var(--outline-color);
}

/* Links */
a {
  text-decoration: none;
  color: inherit;
}

a:hover {
  color: var(--color-accent);
}

/* Buttons  */
.btn {
  font-size: 2rem;
  padding: 1rem 2.5rem;
  border-radius: 8px;
  cursor: pointer;
  font-weight: 600;
  transition: transform 0.4s ease, background 0.3s, color 0.4s;
  border: 0;
}

.btn--outline {
  color: #fff;
}

.btn--solid {
  background: #fff;
  color: var(--color-primary);
}

.btn--accent {
  background: rgb(3, 112, 221);
  color: #e8e7e7;
}

/* .btn:hover {
  transform: scale(1.08);
} */

.btn--solid:hover {
  background: #ffffffed;
}

.btn--accent:hover {
  background: rgb(4, 100, 197);
}

.btn--outline:hover {
  background: rgba(255, 255, 255, 0.212);
}

.fa-play {
  padding-right: 2rem;
}

/* call outs */
.callout {
  color: #fff;
  padding: 0.5rem 1.5rem;
  border-radius: 999px;
  font-size: 1.3rem;
  font-weight: 500;
  transition: border 0.3s;
}

.callout:hover {
  border: 1px solid var(--color-accent);
}

/* Navigation-Bar */

.nav {
  display: flex;
  justify-content: space-between;
  flex-wrap: wrap;
  align-items: center;
  margin: 0;
  border: 0;
  padding: 0 30px;
  background-color: transparent;
  font-size: 2rem;
}

.nav__list {
  display: flex;
  width: auto;
}

.nav__item {
  padding: 0.5rem 3rem;
  transition: all 0.3s ease-in-out;
}

.nav__item:hover {
  background: rgba(255, 255, 255, 0.212);
  color: var(--color-accent);
  border-radius: 8px;
}

/* Featured Content */
.featured-content {
  background: linear-gradient(to bottom, rgba(0, 0, 0, 0), #151515),
    url(/99437ce71df23998cf35ba8f4aaaaf70.jpg);
  height: 75vh;
  min-width: 800px;
  padding-top: 0;
  background-size: cover;
  background-position: center;
  font-size: 20px;
}

/* Description */
.description {
  display: flex;
  flex-direction: column;
  margin-top: 200px;
  padding-left: 20px;
}

.description p {
  padding-top: 2rem;
  width: 40ch;
  overflow: hidden;
  text-overflow: ellipsis;
  display: -webkit-box;
  -webkit-box-orient: vertical;
  -webkit-line-clamp: 4;
}

.buttons button {
  margin-right: 2rem;
}

/* Card */
.card {
  background: url(/050b7e0a06151a70a3d14014a458acac.jpg);
  height: 35rem;
  margin-right: 15px;
  min-width: 250px;
  background-size: cover;
  background-position: center;
  border-radius: 5px;
  transition: all 0.5s ease-in-out;
  position: relative;
}

.card:hover {
  transform: scale(1.1);
  margin: 0 30px;

  .title,
  .rating {
    opacity: 1;
    transform: scale(0.999);
  }
}

.title,
.rating {
  transition: all 0.3s ease-in-out;
  padding: 1rem;
}

.title {
  position: absolute;
  font-size: 1.8rem;
  font-weight: 600;
  bottom: 150px;
  text-align: center;
  overflow: hidden;
  width: 100%;
  opacity: 0;
}

.rating {
  position: absolute;
  bottom: 0;
  right: 0;
  font-size: 1.6rem;
  opacity: 0;
  overflow: hidden;
  font-weight: 600;
  border-radius: 5px;
}

.fa-star {
  color: goldenrod;
  margin-right: 5px;
}

/* Movie List */

.movies {
  padding-left: 30px;
}

.movie-list {
  display: flex;
  transform: translateX(0);
  transition: all 0.5s ease;
}

.movie-list-wrapper {
  height: 60rem;
  position: relative;
  overflow: hidden;
}

.arrow {
  font-size: 70px;
  position: absolute;
  padding: 30px;
  top: 230px;
  right: 0;
  transition: all 0.3s;
  border-radius: 10px;
}

.arrow:hover {
  transform: scale(1.1);
}

/* Search  */

.body-search {
  background-color: #0d0d0d;
}

.search-content {
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
}

.search-section {
  padding-bottom: 10rem;
}

.search-section h2 {
  margin-bottom: 10rem;
}

.search {
  max-width: 800px;
  display: flex;
  border-radius: 30px;
  padding: 2rem;
}

.search .search-input,
.btn--accent {
  font-size: 1.9rem;
  border-radius: 30px;
}

.search .btn--accent {
  padding: 0 5rem;
}

.search-input {
  width: 100%;
  border-radius: 8px;
  padding: 2.5rem;
  outline: 0;
  flex-grow: 1;
  margin-right: 3px;
  color: var(--color-accent);
}

.search-result-container {
  display: grid;
  grid-template-columns: repeat(5, 1fr);
  gap: 20px;
  padding: 5rem;
  transition: all 0.3s ease-in-out;
}

.search-item {
  min-width: 250px;
  height: 35rem;
  position: relative;
  transition: all 0.3s ease-in-out;
  cursor: pointer;
}

.search-image {
  width: 100%;
  height: 100%;
  object-fit: cover;
  border-radius: 5px;
}

.search-title {
  position: absolute;
  font-size: 1.8rem;
  font-weight: 600;
  bottom: 150px;
  left: 0;
  text-align: center;
  overflow: hidden;
  width: 100%;
  padding: 0.5rem;
  opacity: 0;
}

.search-rating {
  position: absolute;
  bottom: 0;
  right: 0;
  font-size: 1.6rem;
  font-weight: 600;
  border-radius: 5px;
  padding: 0.5rem;
  opacity: 0;
}

.search-item:hover .search-title,
.search-item:hover .search-rating {
  opacity: 1;
  transition: all 0.3s ease-in-out;
}

.search-item:hover .search-image {
  opacity: 0.7;
  transition: all 0.3s ease-in-out;
}

/* footer */
.footer {
    background-color: black;
    padding: 5rem;
    color: #b7b5b5;
    text-align: left;
    font-size: 1.8rem;
}

.logo {
    height: 4rem;
}

